mapper.insert:
insert into words_meaning(wordId, meaning, partOfSpeech)
values(
        (
          select wordId
          from words
          where word = #{word}
        ),
        #{meaning},
        #{partOfSpeech}
      )

mapper.select:
select count(*)
from words
where word=#{word}

select wordId
from words
where word = #{word}
