mapper.select:
select f.userId as "targetUserId", feedId, nickname, profileImageUri, ( select count(*) from hearts where feedId = f.feedId ) as "heartCount", ( select count(*) from comments where feedId = f.feedId ) as "commentCount", content, registrationDate, IF(f.userId = #{userId},true,false) as "myFeed" from feeds f join users u on f.userId = u.userId
where f.userId = any( select neighborId from neighbors where userId = #{userId} )
where f.userId = #{neighborId}
where f.userId = #{userId}

select feedImageUri
from feed_images
where feedId = #{feedId}
