mapper.select:
SELECT G.groupId, G.groupName, count(*) as currentCount, G.limitCount, U.nickname as leader, G.description, G.targetCycle, G.targetTime, G.groupDate FROM groups AS G LEFT JOIN group_relationships GR ON G.groupId = GR.groupId LEFT JOIN rooms R ON G.groupId = R.groupId LEFT JOIN users U ON R.userId = U.userId
GR.userId = #{userId}
LOWER(G.groupName) LIKE LOWER(CONCAT('%', #{keyword}, '%')) OR LOWER(U.nickname) LIKE LOWER(CONCAT('%', #{keyword}, '%')) OR LOWER(G.description) LIKE LOWER(CONCAT('%', #{keyword}, '%'))
GROUP BY G.groupId;
