mapper.update:
UPDATE sentences
SET sentenceAudioUri=concat("/study/sentence/audio/",LAST_INSERT_ID(),".mp3")
where sentenceId = LAST_INSERT_ID();

mapper.select:
select sentenceId
from sentence_books
where userId = #{userId}

select s.sentenceId, sentence, meaning, sentenceType, sentenceAudioUri
from sentences s

  join sentence_books b
  on s.sentenceId = b.sentenceId
  where userId = #{userId}

order by ${optionDto.order} ${optionDto.sort}
limit ${optionDto.start},${optionDto.end}
