mapper.select:
select sentenceId
from sentence_books
where userId = #{userId}

select s.sentenceId, sentence, meaning, sentenceType, sentenceAudioUri
from sentences s


    where 1=1


  join sentence_books b
  on s.sentenceId = b.sentenceId
  where userId = #{userId}


  where sentenceType = #{optionDto.filter}


order by ${optionDto.order} ${optionDto.sort}
limit ${optionDto.start},${optionDto.end}

mapper.delete:
delete
from sentence_books
where userId = #{userId} and sentenceId = #{sentenceId}

mapper.select:
select count(*)
from word_sentences
where sentenceId = #{sentenceId}

select sentenceId
from lyric_sentence
where lyricId = #{lyricId}

select lyricId
from lyric_sentence
where sentenceId = #{sentenceId}
