mapper.select:
select *
from words


        level = #{level}


    and wordId = any(
        select wordId
        from word_books
        where userId = #{userId}
      )


order by ${optionDto.order} ${optionDto.sort}

mapper.insert:
insert into words_meaning(wordId, meaning, partOfSpeech)
values(
        (
          select wordId
          from words
          where word = #{word}
        ),
        #{meaning},
        #{partOfSpeech}
      )

mapper.select:
select count(*)
from words
where word=#{word}
