import React from 'react';
import PropTypes from 'prop-types';

const Textbox = ({ type, text, color }) => {
  let style = {};

  switch (type) {
    case 'acceptText':
      style = { color: 'green' };
      break;
    case 'cancelText':
    case 'denyText':
      style = { color: 'red' };
      break;
    default:
      style = {};
    }

return (
  <div className="atom-text-box" style={style}>
  {type === 'doubleText' ? (
    <>
      <div>{text[0]}</div>
      <div>{text[1]}</div>
    </>
  ) : (
    <div>{text}</div>
  )}
  </div>
  );
};

Textbox.propTypes = {
  type: PropTypes.oneOf([
  'noticeText',
  'singleText',
  'doubleText',
  'logoText',
  'acceptText',
  'cancelText',
  'denyText',
  ]).isRequired,
  text: PropTypes.string.isRequired,
  color: PropTypes.string,
};

Textbox.defaultProps = {
  color: 'black',
};

export default Textbox;
