import React, { useState } from 'react';
import PropTypes from 'prop-types';

// 텍스트 상자의 React 컴포넌트 정의
const text = ({ initialValue, onChange }) => {
  const [value, setValue] = useState(initialValue);

  const handleInputChange = (event) => {
    const newValue = event.target.value;
    setValue(newValue);

    // 부모 컴포넌트에 값 변경을 알리기 위한 콜백 호출
    if (onChange) {
      onChange(newValue);
    }
  };

  return (
    <input
      type="text"
      className="atom-text-box" // 추가적인 스타일 클래스를 원자에 대한 클래스로 간주
      value={value}
      onChange={handleInputChange}
    />
  );
};

// PropTypes를 사용하여 컴포넌트의 속성에 대한 타입 검사
text.propTypes = {
  initialValue: PropTypes.string,
  onChange: PropTypes.func,
};

export default text;
